    %% set_prolog_flag(gc, true),  % TODO: tune GC for performance
    %% set_prolog_flag(agc_margin, 0),  % TODO: tune GC for performance
    %% set_prolog_flag(trace_gc, true),  % TODO: delete
    on_signal(int, _, throw),  % TODO: delete?
